import { login, logout, getInfo } from '@/api/user'
import { getToken, setToken, removeToken } from '@/utils/auth'
import router, { resetRouter } from '@/router'
import { changePassword, deleteAccount } from '@/api/security'

const state = {
  token: getToken(),
  name: '',
  avatar: '',
  roles: []
}

const mutations = {
  SET_TOKEN: (state, token) => {
    state.token = token
  },
  SET_NAME: (state, name) => {
    state.name = name
  },
  SET_AVATAR: (state, avatar) => {
    state.avatar = avatar
  },
  SET_ROLES: (state, roles) => {
    state.roles = roles
  }
}

const actions = {
  async login({ commit }, userInfo) {
    const { username, password } = userInfo
    const response = await login({ username: username.trim(), password: password })
    const { token } = response
    commit('SET_TOKEN', token)
    setToken(token)
  },

  async getInfo({ commit, state }) {
    const user = await getInfo()
    const { roles, nickname, avatar } = user

    if (!roles || roles.length <= 0) {
      throw new Error('getInfo: roles must be a non-null array!')
    }

    commit('SET_ROLES', roles)
    commit('SET_NAME', nickname)
    commit('SET_AVATAR', avatar)
    return user
  },

  async logout({ commit, state, dispatch }) {
    await logout()
    commit('SET_TOKEN', '')
    commit('SET_ROLES', [])
    removeToken()
    resetRouter()
    router.push(`/login?redirect=${router.currentRoute.value.fullPath}`)
  },

  async resetToken({ commit }) {
    commit('SET_TOKEN', '')
    commit('SET_ROLES', [])
    removeToken()
  },

  changePassword({ commit }, passwordData) {
    return new Promise((resolve, reject) => {
      changePassword(passwordData)
        .then(() => {
          resolve()
        })
        .catch(error => {
          reject(error)
        })
    })
  },

  deleteAccount({ commit }, password) {
    return new Promise((resolve, reject) => {
      deleteAccount(password)
        .then(response => {
          commit('SET_TOKEN', '')
          commit('SET_ROLES', [])
          removeToken()
          resolve(response)
        })
        .catch(error => {
          reject(error)
        })
    })
  }
}

export default {
  namespaced: true,
  state,
  mutations,
  actions
}

