    console.error('数据库查询错误:', error.message);
    throw error;
  }
}

/**
 * 执行SQL查询 - 返回单行结果
 * @param {string} sql SQL语句
 * @param {Array} params 参数
 * @returns {Promise<Object|null>} 查询结果
 */
async function getOne(sql, params = []) {
