\`\`\`dockerfile
# 不好的做法
RUN apt-get update
RUN apt-get install -y curl
RUN apt-get install -y vim

# 好的做法
RUN apt-get update && \\
    apt-get install -y curl vim && \\
    rm -rf /var/lib/apt/lists/*
\`\`\`

