    console.error('小程序错误:', msg)
  },

  getSystemInfo() {
    wx.getSystemInfo({
      success: (res) => {
        this.globalData.systemInfo = res
        console.log('系统信息:', res)
      }
    })
  },

  checkLoginStatus() {
    const token = wx.getStorageSync(config.storage.token)
    const userInfo = wx.getStorageSync(config.storage.userInfo)

    if (token && userInfo) {
      this.globalData.token = token
      this.globalData.userInfo = userInfo
      console.log('用户已登录')
    } else {
      console.log('用户未登录')
    }
  },

  checkForUpdate() {
    if (wx.canIUse('getUpdateManager')) {
      const updateManager = wx.getUpdateManager()

      updateManager.onCheckForUpdate((res) => {
        console.log('检查更新结果:', res.hasUpdate)
      })

      updateManager.onUpdateReady(() => {
        wx.showModal({
          title: '更新提示',
          content: '新版本已经准备好，是否重启应用？',
          success: (res) => {
            if (res.confirm) {
              updateManager.applyUpdate()
            }
          }
        })
      })

      updateManager.onUpdateFailed(() => {
        wx.showModal({
          title: '更新失败',
          content: '新版本下载失败，请检查网络后重试',
          showCancel: false
        })
      })
    }
  },

  showLoading(title = '加载中...') {
    wx.showLoading({
      title: title,
      mask: true
    })
  },

  hideLoading() {
    wx.hideLoading()
  },

  showToast(title, icon = 'none', duration = 2000) {
    wx.showToast({
      title: title,
      icon: icon,
      duration: duration
    })
  },

  showModal(title, content) {
    return new Promise((resolve) => {
      wx.showModal({
        title: title,
        content: content,
        success: (res) => {
          resolve(res.confirm)
        }
      })
    })
  },

  login() {
    return new Promise((resolve, reject) => {
      wx.login({
        success: (res) => {
          if (res.code) {
            this.getUserInfo(res.code)
              .then(resolve)
              .catch(reject)
          } else {
            reject(new Error('登录失败：' + res.errMsg))
          }
        },
        fail: reject
      })
    })
  },

  getUserInfo(code) {
    return new Promise((resolve, reject) => {
      wx.request({
        url: `${config.api.baseUrl}/auth/wechat-login`,
        method: 'POST',
        data: { code },
        success: (res) => {
          if (res.data.success) {
            const { token, userInfo } = res.data.data

            wx.setStorageSync(config.storage.token, token)
            wx.setStorageSync(config.storage.userInfo, userInfo)

            this.globalData.token = token
            this.globalData.userInfo = userInfo

            resolve({ token, userInfo })
          } else {
            reject(new Error(res.data.message))
          }
        },
        fail: reject
      })
    })
  },

  logout() {
    wx.removeStorageSync(config.storage.token)
    wx.removeStorageSync(config.storage.userInfo)

    this.globalData.token = null
    this.globalData.userInfo = null

    wx.reLaunch({
      url: '/pages/user/login'
    })
  }
})

