  <img src="${avatarDataUrl}" alt="默认头像" width="100" height="100">
</body>
</html>
`;

fs.writeFileSync(path.join(__dirname, 'create-avatar.html'), htmlContent);
console.log('已生成HTML文件，请打开并保存图像');

