/* 现代化导航栏样式 */

/* 针对H5环境的现代化tabBar增强 */
/* #ifdef H5 */

/* 主容器样式优化 */
.uni-tabbar {
  backdrop-filter: blur(20px) !important;
  -webkit-backdrop-filter: blur(20px) !important;
  background: rgba(250, 250, 250, 0.85) !important;
  border-top: 0.5px solid rgba(0, 0, 0, 0.05) !important;
  box-shadow: 0 -1px 20px rgba(0, 0, 0, 0.08) !important;
}

/* 导航项现代化效果 */
.uni-tabbar-item {
  position: relative !important;
  transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94) !important;
}

.uni-tabbar-item:hover {
  transform: translateY(-2px) !important;
  background: rgba(0, 122, 255, 0.05) !important;
  border-radius: 12px !important;
}

/* 激活状态的现代化设计 */
.uni-tabbar-item.uni-tabbar-item-active {
  background: linear-gradient(135deg, rgba(0, 122, 255, 0.1), rgba(0, 122, 255, 0.05)) !important;
  border-radius: 12px !important;
  transform: translateY(-1px) !important;
}

/* 文本样式现代化 */
.uni-tabbar-item .uni-tabbar-item-text {
  font-weight: 500 !important;
  font-size: 10px !important;
  letter-spacing: 0.2px !important;
  margin-top: 2px !important;
  transition: all 0.3s ease !important;
}

.uni-tabbar-item.uni-tabbar-item-active .uni-tabbar-item-text {
  color: #007AFF !important;
  font-weight: 600 !important;
  text-shadow: 0 0 8px rgba(0, 122, 255, 0.3) !important;
}

/* emoji图标现代化增强 */
.uni-tabbar-item-text::first-letter {
  font-size: 18px !important;
  line-height: 1 !important;
  margin-right: 2px !important;
  filter: drop-shadow(0 1px 2px rgba(0, 0, 0, 0.1)) !important;
  transition: all 0.3s ease !important;
}

.uni-tabbar-item.uni-tabbar-item-active .uni-tabbar-item-text::first-letter {
  transform: scale(1.15) !important;
  filter: drop-shadow(0 2px 8px rgba(0, 122, 255, 0.4)) !important;
}

/* 现代化激活指示器 */
.uni-tabbar-item.uni-tabbar-item-active::before {
  content: '' !important;
  position: absolute !important;
  top: 4px !important;
  left: 50% !important;
  transform: translateX(-50%) !important;
  width: 24px !important;
  height: 2px !important;
  background: linear-gradient(90deg, #007AFF, #5AC8FA) !important;
  border-radius: 1px !important;
  box-shadow: 0 0 8px rgba(0, 122, 255, 0.5) !important;
  animation: pulse 2s infinite !important;
}

/* 脉冲动画 */
@keyframes pulse {
  0%, 100% {
    opacity: 1;
    transform: translateX(-50%) scale(1);
  }
  50% {
    opacity: 0.7;
    transform: translateX(-50%) scale(0.95);
  }
}

/* 微交互动画 */
.uni-tabbar-item:active {
  transform: translateY(0) scale(0.95) !important;
}

/* 深色模式适配 */
@media (prefers-color-scheme: dark) {
  .uni-tabbar {
    background: rgba(28, 28, 30, 0.85) !important;
    border-top-color: rgba(255, 255, 255, 0.1) !important;
  }
  
  .uni-tabbar-item:hover {
    background: rgba(0, 122, 255, 0.15) !important;
  }
  
  .uni-tabbar-item.uni-tabbar-item-active {
    background: linear-gradient(135deg, rgba(0, 122, 255, 0.2), rgba(0, 122, 255, 0.1)) !important;
  }
}

/* 平滑过渡效果 */
* {
  -webkit-tap-highlight-color: transparent !important;
}

/* #endif */

/* 全平台通用样式 */
page {
  --tabbar-text-color: #8E8E93;
  --tabbar-active-color: #007AFF;
  --tabbar-bg-color: #FAFAFA;
}

/* 导航栏图标样式 */

/* 基础图标样式 */
.tabbar-icon {
  position: relative;
  display: inline-block;
  width: 22px;
  height: 22px;
  margin-bottom: 2px;
}

.tabbar-icon::before,
.tabbar-icon::after {
  content: '';
  position: absolute;
  background-color: currentColor;
}

/* 首页图标 - 房屋 */
.icon-home {
  position: relative;
}

.icon-home::before {
  content: '';
  position: absolute;
  top: 8px;
  left: 2px;
  width: 18px;
  height: 12px;
  border: 2px solid currentColor;
  border-top: none;
  background: transparent;
}

.icon-home::after {
  content: '';
  position: absolute;
  top: 2px;
  left: 7px;
  width: 8px;
  height: 8px;
  border-left: 2px solid currentColor;
  border-right: 2px solid currentColor;
  border-top: 2px solid currentColor;
  transform: rotate(45deg);
  background: transparent;
}

/* 刷题图标 - 文档 */
.icon-practice {
  position: relative;
}

.icon-practice::before {
  content: '';
  position: absolute;
  top: 2px;
  left: 4px;
  width: 14px;
  height: 18px;
  border: 2px solid currentColor;
  background: transparent;
}

.icon-practice::after {
  content: '';
  position: absolute;
  top: 6px;
  left: 7px;
  width: 8px;
  height: 1px;
  background: currentColor;
  box-shadow: 
    0 3px 0 currentColor,
    0 6px 0 currentColor;
}

/* 考试图标 - 剪贴板 */
.icon-exam {
  position: relative;
}

.icon-exam::before {
  content: '';
  position: absolute;
  top: 4px;
  left: 3px;
  width: 16px;
  height: 16px;
  border: 2px solid currentColor;
  background: transparent;
}

.icon-exam::after {
  content: '';
  position: absolute;
  top: 8px;
  left: 6px;
  width: 2px;
  height: 2px;
  background: currentColor;
  box-shadow: 
    4px 0 0 currentColor,
    8px 0 0 currentColor,
    0 3px 0 currentColor,
    4px 3px 0 currentColor,
    8px 3px 0 currentColor,
    0 6px 0 currentColor,
    4px 6px 0 currentColor,
    8px 6px 0 currentColor;
}

/* 个人中心图标 - 用户 */
.icon-profile {
  position: relative;
}

.icon-profile::before {
  content: '';
  position: absolute;
  top: 2px;
  left: 8px;
  width: 6px;
  height: 6px;
  border: 2px solid currentColor;
  border-radius: 50%;
  background: transparent;
}

.icon-profile::after {
  content: '';
  position: absolute;
  top: 12px;
  left: 4px;
  width: 14px;
  height: 8px;
  border: 2px solid currentColor;
  border-radius: 7px 7px 0 0;
  background: transparent;
}

/* 激活状态样式 */
.tabbar-icon.active {
  color: #4A90E2;
}

.tabbar-icon.inactive {
  color: #7A7E83;
}

/* 动画效果 */
.tabbar-icon {
  transition: all 0.3s ease;
}

.tabbar-icon:hover {
  transform: scale(1.1);
}

/* 特殊效果 - 激活时的背景光晕 */
.tabbar-icon.active::before {
  box-shadow: 0 0 8px rgba(74, 144, 226, 0.3);
}

/* TabBar 自定义样式 */

/* H5端tabBar样式优化 */
/* #ifdef H5 */
.uni-tabbar {
  height: 100px !important;
}

.uni-tabbar-item {
  height: 100px !important;
  display: flex !important;
  flex-direction: column !important;
  justify-content: center !important;
  align-items: center !important;
  padding: 12px 8px 16px 8px !important;
}

.uni-tabbar-item-text {
  font-size: 11px !important;
  line-height: 1.2 !important;
  white-space: pre-line !important;
  text-align: center !important;
  margin-top: 6px !important;
}

/* 增大Unicode字符图标 */
.uni-tabbar-item-text::first-line {
  font-size: 28px !important;
  font-weight: bold !important;
  line-height: 1 !important;
}

/* 选中状态样式 */
.uni-tabbar-item.uni-tabbar-item-active .uni-tabbar-item-text {
  color: #007AFF !important;
}

.uni-tabbar-item.uni-tabbar-item-active .uni-tabbar-item-text::first-line {
  color: #007AFF !important;
  text-shadow: 0 1px 3px rgba(0, 122, 255, 0.3) !important;
}

/* 未选中状态样式 */
.uni-tabbar-item:not(.uni-tabbar-item-active) .uni-tabbar-item-text {
  color: #8E8E93 !important;
}

.uni-tabbar-item:not(.uni-tabbar-item-active) .uni-tabbar-item-text::first-line {
  color: #8E8E93 !important;
}

/* 点击动效 */
.uni-tabbar-item {
  transition: all 0.2s ease !important;
}

.uni-tabbar-item:active {
  transform: scale(0.95) !important;
}

.uni-tabbar-item-active {
  transform: translateY(-2px) !important;
}

/* 背景样式 */
.uni-tabbar {
  background: linear-gradient(180deg, #ffffff 0%, #f8f9fa 100%) !important;
  border-top: 1px solid rgba(0, 0, 0, 0.08) !important;
  box-shadow: 0 -4px 16px rgba(0, 0, 0, 0.08) !important;
}
/* #endif */

/* 通用样式（多端兼容） */
.tabbar-icon {
  font-size: 28px;
  font-weight: bold;
  line-height: 1;
  margin-bottom: 6px;
}

.tabbar-text {
  font-size: 11px;
  line-height: 1.2;
}

