/**
 * 通用的API响应数据适配器
 * @param {Object} response API响应数据
 * @param {Function} dataAdapter 具体的数据适配函数
 * @returns {Object} 适配后的响应数据
 */
export function adaptApiResponse(response, dataAdapter) {
  if (!response) return null;

  const standardResponse = {
    code: response.code || response.status || 200,
    message: response.message || response.msg || 'success',
    data: response.data || response.result || response
  };

  if (dataAdapter && typeof dataAdapter === 'function') {
    standardResponse.data = dataAdapter(standardResponse.data);
  }

  return standardResponse;
}

/**
 * 全局加载状态管理器
 * 用于统一处理数据加载状态和用户体验
 */

class LoadingManager {
  constructor() {
    this.loadingStates = new Map();
    this.globalLoading = false;
  }

  /**
   * 显示加载状态
   * @param {string} key 加载状态的唯一标识
   * @param {Object} options 加载选项
   */
  show(key = 'default', options = {}) {
    const config = {
      title: '加载中...',
      mask: true,
      showToast: true,
      ...options
    };

    this.loadingStates.set(key, config);

    if (config.showToast && !this.globalLoading) {
      uni.showLoading({
        title: config.title,
        mask: config.mask
      });
      this.globalLoading = true;
    }
  }

  /**
   * 隐藏加载状态
   * @param {string} key 加载状态的唯一标识
   */
  hide(key = 'default') {
    this.loadingStates.delete(key);

    if (this.loadingStates.size === 0 && this.globalLoading) {
      uni.hideLoading();
      this.globalLoading = false;
    }
  }

  /**
   * 隐藏所有加载状态
   */
  hideAll() {
    this.loadingStates.clear();
    if (this.globalLoading) {
      uni.hideLoading();
      this.globalLoading = false;
    }
  }

  /**
   * 检查是否有加载状态
   * @param {string} key 可选的特定加载状态标识
   * @returns {boolean}
   */
  isLoading(key) {
    if (key) {
      return this.loadingStates.has(key);
    }
    return this.loadingStates.size > 0;
  }

  /**
   * 包装异步函数，自动处理加载状态
   * @param {Function} asyncFn 异步函数
   * @param {Object} options 加载选项
   * @returns {Function} 包装后的函数
   */
  wrap(asyncFn, options = {}) {
    const key = options.key || 'default';

    return async (...args) => {
      try {
        this.show(key, options);
        const result = await asyncFn(...args);
        return result;
      } catch (error) {
        if (options.showError !== false) {
          uni.showToast({
            title: error.message || '操作失败',
            icon: 'none',
            duration: 2000
          });
        }
        throw error;
      } finally {
        this.hide(key);
      }
    };
  }
}

const loadingManager = new LoadingManager();

export const showLoading = (key, options) => loadingManager.show(key, options);
export const hideLoading = (key) => loadingManager.hide(key);
export const hideAllLoading = () => loadingManager.hideAll();
export const isLoading = (key) => loadingManager.isLoading(key);
export const wrapLoading = (asyncFn, options) => loadingManager.wrap(asyncFn, options);

export default loadingManager;

import config from '../config/index.js'

class Request {
  constructor() {
    this.baseUrl = config.api.baseUrl
    this.timeout = config.api.timeout
  }

  request(options) {
    return new Promise((resolve, reject) => {
      if (options.loading !== false) {
        uni.showLoading({
          title: options.loadingText || '请求中...',
          mask: true
        })
      }

      const token = uni.getStorageSync(config.storage.token)

      const header = {
        'Content-Type': 'application/json',
        ...options.header
      }

      if (token) {
        header.Authorization = `Bearer ${token}`
      }

      uni.request({
        url: this.baseUrl + options.url,
        method: options.method || 'GET',
        data: options.data || {},
        header: header,
        timeout: options.timeout || this.timeout,
        success: (res) => {
          if (options.loading !== false) {
            uni.hideLoading()
          }

          if (res.statusCode === 200) {
            const data = res.data
            if (data.success === true || data.code === 200 || data.status === 'success' || (data.code === undefined && data.success === undefined)) {
              resolve(data)
            } else {
              this.handleBusinessError(data, reject)
            }
          } else if (res.statusCode === 401) {
            this.handleUnauthorized()
            reject(new Error('登录已过期，请重新登录'))
          } else {
            this.handleHttpError(res.statusCode, reject)
          }
        },
        fail: (error) => {
          if (options.loading !== false) {
            uni.hideLoading()
          }

          this.handleNetworkError(error, reject)
        }
      })
    })
  }

  handleBusinessError(data, reject) {
    const message = data.message || '请求失败'

    if (!data.silent) {
      uni.showToast({
        title: message,
        icon: 'none',
        duration: 2000
      })
    }

    reject(new Error(message))
  }

  handleHttpError(statusCode, reject) {
    let message = '请求失败'

    switch (statusCode) {
      case 400:
        message = '请求参数错误'
        break
      case 401:
        message = '未授权访问'
        break
      case 403:
        message = '禁止访问'
        break
      case 404:
        message = '请求的资源不存在'
        break
      case 500:
        message = '服务器内部错误'
        break
      case 502:
        message = '网关错误'
        break
      case 503:
        message = '服务不可用'
        break
      default:
        message = `请求失败(${statusCode})`
    }

    uni.showToast({
      title: message,
      icon: 'none',
      duration: 2000
    })

    reject(new Error(message))
  }

  handleNetworkError(error, reject) {
    let message = '网络连接失败'

    if (error.errMsg) {
      if (error.errMsg.includes('timeout')) {
        message = '请求超时，请检查网络'
      } else if (error.errMsg.includes('fail')) {
        message = '网络连接失败，请检查网络'
      }
    }

    uni.showToast({
      title: message,
      icon: 'none',
      duration: 2000
    })

    reject(new Error(message))
  }

  handleUnauthorized() {
    uni.removeStorageSync(config.storage.token)
    uni.removeStorageSync(config.storage.userInfo)

    const app = getApp()
    app.globalData.token = null
    app.globalData.userInfo = null

    uni.reLaunch({
      url: '/pages/auth/login'
    })
  }

  get(url, data = {}, options = {}) {
    return this.request({
      url,
      method: 'GET',
      data,
      ...options
    })
  }

  post(url, data = {}, options = {}) {
    return this.request({
      url,
      method: 'POST',
      data,
      ...options
    })
  }

  put(url, data = {}, options = {}) {
    return this.request({
      url,
      method: 'PUT',
      data,
      ...options
    })
  }

  delete(url, data = {}, options = {}) {
    return this.request({
      url,
      method: 'DELETE',
      data,
      ...options
    })
  }

  uploadFile(url, filePath, name = 'file', formData = {}) {
    return new Promise((resolve, reject) => {
      uni.showLoading({
        title: '上传中...',
        mask: true
      })

      const token = uni.getStorageSync(config.storage.token)
      const header = {}

      if (token) {
        header.Authorization = `Bearer ${token}`
      }

      uni.uploadFile({
        url: this.baseUrl + url,
        filePath: filePath,
        name: name,
        formData: formData,
        header: header,
        success: (res) => {
          uni.hideLoading()

          try {
            const data = JSON.parse(res.data)
            if (data.success) {
              resolve(data)
            } else {
              uni.showToast({
                title: data.message || '上传失败',
                icon: 'none'
              })
              reject(new Error(data.message || '上传失败'))
            }
          } catch (error) {
            uni.showToast({
              title: '上传失败',
              icon: 'none'
            })
            reject(error)
          }
        },
        fail: (error) => {
          uni.hideLoading()
          uni.showToast({
            title: '上传失败',
            icon: 'none'
          })
          reject(error)
        }
      })
    })
  }
}

